$('#nos_ligamos_form').submit(function(){
  $('#nos_ligamos_form input[type=submit]').attr('value','enviando...');
  $.ajax({
   type: "POST",
   url: 'contato/nos_ligamos',
   data: $(this).serialize(),
   success: function(data){
    alert(data);
    if(data == "Mensagem enviada com sucesso."){
     $('#nos_ligamos_form input[type=submit]').attr('value','enviado');
     $('#nos_ligamos_form input[type=submit]').attr('disabled',true);
     $(this).reset();
    }else{
    $('#nos_ligamos_form input[type=submit]').val('ok');
      }
   }
 });
  return false;
});

$('.form_news').submit(function (){
$('.form_news input[type=submit]').attr('value','cadastrando...');
$.ajax({
   type: "POST",
   url: 'newsletter/cadastrar',
   data: $(this).serialize(),
   success: function(data){
    alert(data);
    if(data == "Obrigado por cadastrar seu e-mail em nosso sistema de NewsLatter!"){
     $('.form_news input[type=submit]').attr('value','cadastrado');
     $('.form_news input[type=submit]').attr('disabled',true);
     $(this).reset();
    }else{
        $('.form_news input[type=submit]').val('ok');
      }
   }
 });
return false;
});

