      var map;
      function initialize() {
        var point = new google.maps.LatLng(-26.307717,-48.848573);
        var myMapOptions = {
          zoom: 16,
          center: point,
          mapTypeId: google.maps.MapTypeId.ROADMAP
        };

        var map = new google.maps.Map(document.getElementById("google-maps"),myMapOptions);
        // var image = new google.maps.MarkerImage('libs/imgs/maps.image.png');
        // var shadow = new google.maps.MarkerImage('libs/imgs/maps.shadow.png');

        var marker = new google.maps.Marker({
          // icon: image,
          // shadow: shadow,
          map: map,
          position: point
        });
      }

      google.maps.event.addDomListener(window, 'load', initialize);
      
      
        function ClearPlaceHolder (input) {
            if (input.value == input.defaultValue) {
                input.value = "";
                $(input).css('color','#000');
            }
        }
        function SetPlaceHolder (input) {
          $(input).css('color','#000');
            if (input.value == "") {
                input.value = input.defaultValue;
                $(input).css('color','#c8c8c8');
            }
        }
        
        document.login.onsubmit=function(){
          var form = this;
          setTimeout(function(){form.reset()},1000);
        }
        
        
