div.script:
$(document).ready(function(){

   $('#form-noticia-filtro').submit(function(){
     var condicao = ''; 
     var keyword = $('#filtro-keyword').val();
     if(keyword.length < 3){
      alert('Favor informar ao menos 3 caracteres para busca');
      $('#filtro-keyword').val('');
      $('#filtro-keyword').focus();
      return false;
     }
     if(keyword != 'Palavra-chave' && keyword){
      condicao += 'keyword/'+keyword+'/';
     }

     var data = $('.filtro #filtro-data').val();
     if(data != 'Escolha o Período' && data){
      data = data.replace('/','-').replace('/','-').replace('/','-').replace('/','-').replace('/','-');
      condicao+='filtro/'+data+'/';
     }

     var categoria = new Number($('#categoria').val());
     if(categoria != '' && categoria > 0){           
      condicao+='categoria/'+categoria+'/';
     } 

    location = 'http://www.joinville.sc.gov.br/noticia/index/'+condicao; 
    return false;
   });
 $('.filtro #filtro-data').val('Escolha o Período');

});

body.script:

var _gaq = _gaq || [];
_gaq.push(['_setAccount', 'UA-24546014-1']);
_gaq.push(['_trackPageview']);
(function() {
 var ga = document.createElement('script'); ga.type = 'text/javascript'; ga.async = true;
 ga.src = ('https:' == document.location.protocol ? 'https://ssl' : 'http://www') + '.google-analytics.com/ga.js';
 var s = document.getElementsByTagName('script')[0]; s.parentNode.insertBefore(ga, s);
})();

document.onready = (function(){
   $('#news-letter').submit(function(){
    var nome   = $('#nome-mail').val();
    var sEmail = $('#e-mail').val(); 

    er = /^[a-zA-Z0-9][a-zA-Z0-9\._-]+@([a-zA-Z0-9\._-]+\.)[a-zA-Z-0-9]{2}/;

     if(nome.length > 100){
     alert('Nome não pode ter mais de 100 caracteres');
     return false;
     }

     if(!er.exec(sEmail)){
      alert('Email Invalido');
      return false;
    }   
    $.ajax({
     type: 'POST',
     url: $('#news-letter').attr('action'),
     data: 'mail='+sEmail+'&name='+nome,
     success: function(msg){
       alert( msg );
       $('#nome-mail').val('Nome');
       $('#e-mail').val('E-mail');
     }
    });             
    return false;
   });     
});
