link.script:
//*************************************************************************************
$('document').ready(function(){
 //-------------------------------------------- links
 $('#loading').hide();
 //----
 $('#btn_filtro').click(function(){
 
   scroll(0,0);
   var arr_nivel = new Array();
   var nivel  = "";
   var nivel_id = "";
   
   if($('#hdd_filtro_unidade').val()!=""){
    arr_nivel = $('#hdd_filtro_unidade').val().split("_");
    nivel  = arr_nivel[0];
    nivel_id = arr_nivel[1];
   }//if
   
   $('#divViewRegistro').empty().html("");
   $('#divViewRegistro').hide();
   $('#divViewExtra').empty().html("");
   $('#divViewExtra').hide();
   
   $('#divFormListagem').show();

   $('#loading').ajaxStart(function(){
     $('#listBusca').hide();
     $('#loading').show();
   });
   $('#loading').ajaxStop(function(){
     $('#loading').hide();
   });
   $.post('ajax_news_list.php',
    {
     pagina: $('#hdd_pag_destino').val(),
     nvl:nivel,
     idnvl:nivel_id,
     idlng:$('#hdd_id_idm').val(),
     tipo: $('#hdd_tipo').val(),
     now: getNowString()
    },
    
    function(data){
     $('#listBusca').show();
     $('#listBusca').empty().html(data);
    }
   );
   return false;
 });
 //----
 $('#btn_view').click(function(){
 
   scroll(0,0);
     
   $('#divFormListagem').hide();
   
   //$('#divViewRegistro').show();
     
   $('#loading').ajaxStart(function(){
     $('#divViewRegistro').hide();
     $('#loading').show();
   });
   $('#loading').ajaxStop(function(){
     $('#loading').hide();
   });
   $.post('ajax_news_view.php',
    {
     id: $('#hdd_idctd').val(),
     now: getNowString()
    },
    function(data){
     var arrResultado = data.split("##!##");
     $('#divViewRegistro').show();
     //$('#divViewRegistro').empty().html(arrResultado[0]);
     document.getElementById('divViewRegistro').innerHTML = arrResultado[0];
     $('#divViewExtra').show();
     if(arrResultado.length > 1){
      //$('#divViewExtra').empty().html(arrResultado[1]);
      document.getElementById('divViewExtra').innerHTML = arrResultado[1];
     }
     confereTituloImprensa();
    }
   );
   return false;
 });
 //====================================================<<
  var objClickAnteriorSecao;
  $(function(){
   $("#idLinkSecao").click(function(){
    //se ja foi cliclado  em um anterior seta o novo zindex dos anteriores        
    if(objClickAnteriorSecao){
     objClickAnteriorSecao.css("zIndex","75");
     objClickAnteriorSecao.parents("#boxFiltroSecao").find(".groupOptions").css("zIndex","65"); 
     $("#boxFiltroSecao.groupOptions").fadeOut(100);
    }
    objClickAnteriorSecao = $(this);
    //troca o zindex do clicado para sempre ficar sobre os demais
    $(this).parents("#boxFiltroSecao").find(".groupOptions").css("zIndex","85");
    $(this).css("zIndex","95");  
    //no click do 'a' busca no pai 'groupSelect' um filho 'groupOptions'
    $(this).parents("#boxFiltroSecao").find(".groupOptions").slideToggle();
    return false;
   })
   $("#idGroupSecao.groupOptions ul li a").click(function(){
    
    var txt = $(this).attr("nomecorto");//$(this).text();
    var val = $(this).attr("valor");
    $("#idLinkSecao").html(txt);
    $("#hdd_filtro_unidade").val(val);
    $("#boxFiltroSecao.groupOptions").fadeOut(100);
    //irPagina(1);
    //return false;
    
    var val = $(this).attr("valor");
    
    var arr_nivel = new Array();
    var nivel  = "";
    var nivel_id = "";
    
    if($('#hdd_filtro_unidade').val()!=""){
     arr_nivel = $('#hdd_filtro_unidade').val().split("_");
     nivel  = arr_nivel[0];
     nivel_id = arr_nivel[1];
    }//if
    
    document.location.href = "./?nvl="+nivel+"&idnvl="+nivel_id;
    return false;
   })
  });
 });
//*************************************************************************************
 function confereTituloImprensa(){
  var qual = $('#hdd_idctd').val();
  if(qual==335){
   $('#h1_titConteudoMain').html("Apresentação");
   $('#h2_titConteudo').html('');
   $('#h2_titConteudo').removeClass('titConteudo');
  }else if(qual==340){
   $('#h1_titConteudoMain').html("Perfil Sociesc");
   $('#h2_titConteudo').html('');
   $('#h2_titConteudo').removeClass('titConteudo');
  }
 }
 function irPagina(qual){
  scroll(0,0);
  document.getElementById("hdd_pag_destino").value = qual;
  document.getElementById("btn_filtro").click();
 }
 function setaFiltro(nmcmp,nmcrt,valor){
  document.getElementById("hdd_filtro_unidade").value = valor;
  document.getElementById("idLinkSecao").innerHTML = nmcrt;
  document.getElementById("btn_filtro").click();
 }
 function viewRegistro(qual){
  scroll(0,0);
  document.getElementById("hdd_idctd").value = qual;
  document.getElementById("btn_view").click();
 }
 function activaMenu(qual){
  var titulo = "";
  var tipo = "";
  // 0,all - 1,noticias - 2,agenda
  $("#a_menu_info_0").removeClass("mnHome");
  $("#a_menu_info_1").removeClass("mnHome");
  $("#a_menu_info_2").removeClass("mnHome");
  
  if(qual==1){
   titulo = "Notícias";
   tipo = "N";
  }else if(qual==2){
   titulo = "Agenda/Eventos";
   tipo = "A";
  }else{
   titulo = "Lista Completa";
  }
  
  $("#a_menu_info_"+qual).addClass("mnHome");
  $("#h1_titulo").html(titulo);
  $('#hdd_tipo').val(tipo);
  irPagina(1);
 }//function
 function activaMenuAssessoria(qual){
  var titulo = "";
  var tipo = "";
  var boolFiltra = false;
  // 0,apresentacao - 1,release - 2,clipping - 3,perfil sociesc
  $("#a_menu_info_0").removeClass("mnHome");
  $("#a_menu_info_1").removeClass("mnHome");
  $("#a_menu_info_2").removeClass("mnHome");
  $("#a_menu_info_3").removeClass("mnHome");
  $("#a_menu_info_4").removeClass("mnHome");
  
  if(qual==0){
   titulo = "Apresentação";
   tipo = "SC,SN";
  }else if(qual==1){
   titulo = "Lista Completa";
   tipo = "SC,SN";
   boolFiltra = true;
  }else if(qual==2){
   titulo = "Release";
   tipo = "SN";
   boolFiltra = true;
  }else if(qual==3){
   titulo = "Clippings";
   tipo = "SC";
   boolFiltra = true;
  }else{
   titulo = "Perfil Sociesc";
   tipo = "SC,SN";
  }//if
  
  $("#a_menu_info_"+qual).addClass("mnHome");
  
  if(boolFiltra){
   $("#h1_titulo").html(titulo);
   $('#hdd_tipo').val(tipo);
   irPagina(1);
  }//if
  
 }//function
 function voltaListagem(){
   $('#divViewRegistro').empty().html("");
   $('#divViewRegistro').hide();
   $('#divViewExtra').empty().html("");
   $('#divViewExtra').hide();
   $('#divFormListagem').show();
   $('#listBusca').show();
 }

